import React, {useEffect} from 'react'
import {URL_KEYS} from '../../../Constants'
import {useHistory, useParams} from 'react-router-dom'
import {useGoBack, useUrlParams} from '../../../Hooks/app'
import {useOfferingOrderInOfferPage} from '../../../Hooks/order'
import {useOfferingGallery, useOfferingsCharacs} from '../../../Hooks/offerings'
import {useQuery} from 'react-query'
import orgApi from '../../../Service-v2/organization'
import {AuthModal} from '../../../UIComponents/AuthModal'
import {Gallery, PageBody} from '../organisms'
import {RootContent} from '../../../UIComponents/GlobalStyles'
import {FixedHeader} from '../../../Components/FixedHeader'
import {Col, Row} from 'antd'


export const OfferingPage = () => {
    // useOfferingInfo()
    useOfferingGallery()
    useOfferingsCharacs()
    const {push} = useHistory()
    const {urlData} = useUrlParams()
    const {organization, offering_id} = useParams()
    const {handleRequest, setOpenModal, openModal} = useOfferingOrderInOfferPage()
    const {goBack} = useGoBack({
        pathname: `/${organization}/offerings`
    })

    const {data: res} = useQuery(
        ['/offers/id', offering_id],
        () => orgApi.getOfferingId(offering_id),
        {
            enabled: !!offering_id
        }
    )

    useEffect(() => {
        let timeout = null

        timeout = setTimeout(() => {
            if (!urlData[URL_KEYS.SPECIALIST_ID]) {
                if (res?.data?.responsible?.length > 0) {
                    push(`?${URL_KEYS.SPECIALIST_ID}=${res?.data?.responsible?.[0].id}`)
                }
            }
        }, 300)

        return () => {
            clearTimeout(timeout)
            timeout = null
        }
    }, [res, urlData, push])


    return (
        <RootContent paddingTop={50} height='100vh'>
            <AuthModal
                modalIsOpen={openModal}
                onCancel={() => setOpenModal(false)}
                action={handleRequest}
            />
            <FixedHeader
                title={res?.data?.organization?.name}
                goBack={goBack}
            />
            <Row gutter={[0, 12]}>
                <Col span={24}>
                    <Gallery/>
                </Col>
                <Col span={24}>
                    <PageBody/>
                </Col>
            </Row>
        </RootContent>
    )
}
