import styled from 'styled-components'
import {FixedHeaderWrapper} from '../../../../Components/FixedHeader/style'
import {StyledText, StyledTitle} from '../../../../UIComponents/Typography/style'
import {EmptyContainerWrapper} from '../../../../UIComponents/GlobalStyles'

export const SearchPageWrapper = styled.div`
  height: 100vh;
  overflow-y: auto;
  overflow-x: hidden;

  ${FixedHeaderWrapper} {
    height: auto;
  }

  ${EmptyContainerWrapper} {
    height: 80vh;
    width: 100%;

    ${StyledTitle} {
      font-size: 18px;
      margin-top: 24px;
    }
  }

  .fixed-header-search {
    height: 50px;
    color: var(--dark-dwed);

    input {
      border-radius: 6px;
      border: none;
      width: 100%;

      &:focus {
        outline: none;
      }
    }
  }

  .search-body {
    padding-top: 50px;
  }

  .search-items-block {
    border-top: 1px solid #F2F2F2;

    .search-items-wrapper {
      padding: 0 12px;
    }
  }
`

export const SearchCardWrapper = styled.div`
  ${StyledText} {
    display: -webkit-box;
    overflow: hidden;
    text-overflow: ellipsis;
    -webkit-line-clamp: 1;
    -webkit-box-orient: vertical;
  }
`
