export function merge<T>(
  events: Array<Event<T> | Store<T> | Effect<T, any, any>>,
  config?: object,
): Event<T> {
  const result = createEvent(config || unitObjectName(events, 'merge'))
  assertNodeSet(events, 'merge', 'first argument')
  forward({
    from: events,
    to: result,
    meta: {op: 'merge'},
  })
  return result
}
