import React from 'react'
import {RootContent} from '../../../UIComponents/GlobalStyles'
import {FixedHeader} from '../../../Components/FixedHeader'
import {useGoBack} from '../../../Hooks/app'
import {useHistory, useParams} from 'react-router-dom'
import {useTranslation} from 'react-i18next'
import {useOrderList} from '../../../Hooks/org'
import {useStore} from 'effector-react'
import {$orderModel} from '../../../Models/order-model'
import {Col, Row} from 'antd'
import {RecordsCard} from '../molecules'
import moment from 'moment'

export const AccountRecords = () => {
    useOrderList()
    const {push} = useHistory()
    const {t} = useTranslation()
    const {username} = useParams()
    const {$orderList: {data}} = useStore($orderModel)
    const {goBack} = useGoBack({pathname: `/@${username}`})

    const handlePush = (item) => {
        const responsible = item.responsible.id
        const status = item.status

        if (responsible && status === 5) {
            console.log(`status: ${status}, responsible: ${responsible}`)
            push(`/@${username}/records/${responsible}`)
        }
    }

    return (
        <RootContent
            height='100vh'
            paddingTop={62}
        >
            <FixedHeader
                goBack={goBack}
                title={t('records')}
            />
            <Row gutter={[0, 12]} className='container'>
                {
                    data && data.map((item, idx) => (
                        <Col span={24} key={`${idx + 1}`}>
                            <RecordsCard
                                orgName={item.responsible.org.name}
                                meetTime={moment(item.meet_date).format('DD.MM.YYYY HH:mm')}
                                src={item.responsible.user.avatar}
                                specName={item.responsible.user.full_name}
                                specCat={item.responsible.user.main_cat.name}
                                containerPath={() => handlePush(item)}
                            />
                        </Col>
                    ))
                }
            </Row>
        </RootContent>
    )
}
