import React, {useState} from 'react'
import {Col, Row} from 'antd'
import {useStore} from 'effector-react'
import {DescriptionWrapper, OfferingCreateButton} from '../atoms'
import {URL_KEYS} from '../../../Constants'
import {useTranslation} from 'react-i18next'
import {$appModel} from '../../../Models/app'
import {useUrlGenerate} from '../../../Hooks/org'
import {Button} from '../../../UIComponents/Button'
import {Text} from '../../../UIComponents/Typography/Text'
import {useOfferingOrderInOfferPage} from '../../../Hooks/order'
import {ShortCard, ShortCardSkeleton} from '../../../Components/Cards'
import {DataWrapper, SpecialistNavLink} from '../../../Components/Offering/OrgOfferings/style'
import {OverlaySettings} from '../../../Components/Overlay'
import {OverlayAuth} from '../../../UIComponents/OverlayAuth'
import {useParams} from 'react-router-dom'
import {useOfferingDetail} from '../../../Hooks/offerings'


export const OfferSpecialists = ({setOpenModal, openModal, handleRequest}) => {
    const {t} = useTranslation()
    const {offering_id} = useParams()
    const {$app: {token}} = useStore($appModel)
    const {getActive, generateUrl} = useUrlGenerate()
    const [auth, setAuth] = useState(false)
    const {res, isLoading} = useOfferingDetail(offering_id)

    const handleClose = () => {
        if (auth) {
            setAuth(false)
        }
        setOpenModal(false)
    }


    return (
        <>
            <OverlaySettings
                openSettings={openModal}
                onClose={handleClose}
                content={<OverlayAuth
                    action={handleRequest}
                    auth={auth}
                    setAuth={setAuth}
                    onClose={handleClose}
                />}
            />
            <Row className='container'>
                <Col span={24}>
                    <DescriptionWrapper>
                        <Text>{`${t('select_specialist')}:`}</Text>
                    </DescriptionWrapper>
                </Col>
                <Col span={24}>
                    <DataWrapper>
                        {
                            !isLoading
                                ? (
                                    res?.data?.responsible.map((item, idx) => (
                                        <SpecialistNavLink
                                            key={`${idx + 1}`}
                                            to={generateUrl(URL_KEYS.SPECIALIST_ID, item.id)}
                                            isActive={() => getActive(URL_KEYS.SPECIALIST_ID, item.id)}
                                        >
                                            <ShortCard
                                                imgSize={56}
                                                direction='vertical'
                                                text={item.job?.name ? item.job?.name : 'test cat'}
                                                // imgUrl={item.user.avatar}
                                                name={item.user?.full_name ? item.user?.full_name : 'test'}
                                                // containerPath={`/@${item.user.username}`}
                                                active={getActive(URL_KEYS.SPECIALIST_ID, item.id)}
                                            />
                                        </SpecialistNavLink>
                                    ))
                                ) : (
                                    <ShortCardSkeleton
                                        size={56}
                                        nameWidth={60}
                                        nameHeight={24}
                                        textHeight={16}
                                        textWidth={40}
                                        nameSize={12}
                                        textSize={12}
                                        height={96}
                                        direction='vertical'
                                    />
                                )
                        }
                    </DataWrapper>
                </Col>
            </Row>
            <OfferingCreateButton>
                <Button
                    loading={buttonLoad}
                    variant='primary'
                    className='add-to-cart-button'
                    onClick={() => token ? handleRequest() : setOpenModal(true)}
                >
                    {t('checkout')}
                </Button>
            </OfferingCreateButton>
        </>
    )
}
