import styled from 'styled-components'
import {InputIcon, StyledInput} from '../../../UIComponents/Inputs/style'
import {StyledText} from '../../../UIComponents/Typography/style'


export const SearchInputWrapper = styled.form`

  ${StyledInput} {
    background: var(--basic-grey-bg);
  }

  ${InputIcon} {

    svg {
      width: 14px;
      height: 14px;
    }
  }
`

export const AccountInfoWrapper = styled.div`
  margin-top: 12px;
  box-shadow: 0 4px 24px rgba(38, 38, 38, 0.04);

  .avatar {
    display: flex;
    justify-content: center;
  }

  .about-account {
    text-align: center;

    ${StyledText} {
      color: var(--grey-dwed);
    }
  }
`

export const AccountBlockItems = styled.div`
  box-shadow: 0 4px 24px rgba(38, 38, 38, 0.04);

  .item-description {
    color: var(--grey-dwed);
  }

  .icon {
    display: flex;
    justify-content: center;
    align-items: center;

    svg {
      width: 24px;
      height: 24px;
    }
  }
`
