import React, {useState} from 'react'
import {OfferGroupWrapper, OfferingGroupSearchForm} from '../style'
import {SearchSvg} from '../../../../Icons/Search'
import InfiniteScroll from 'react-infinite-scroll-component'
import {Col, Row} from 'antd'
import {OfferingGroupItem} from '../OfferingGroupItem'
import {useTranslation} from 'react-i18next'
import {useStore} from 'effector-react'
import {$orgModel} from '../../../../Models/org-model'
import {OrgSpecialistCard} from '../../../Cards'

export const OverlaySpecialists = () => {
    const {t} = useTranslation()
    const [search, setSearch] = useState('')
    const {$orgSpecialistsList: {data, result, loading}} = useStore($orgModel)

    return (
        <OfferGroupWrapper id='scrollableDiv'>
            <OfferingGroupSearchForm onSubmit={handleSubmit}>
                <input
                    placeholder={`${t('search')}...`}
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />
                <SearchSvg/>
            </OfferingGroupSearchForm>
            <InfiniteScroll
                next={loadMore}
                dataLength={result?.nextOffset || 20}
                hasMore={!loading && !!result?.next}
                loader={<>...loading</>}
                style={{overflow: 'hidden', padding: '50px 0'}}
                scrollableTarget='scrollableDiv'
            >
                <Row gutter={[12, 12]}>
                    {
                        data && data.map((item, idx) => (
                            <Col
                                className='centered'
                                key={`${idx + 1}`} span={8}
                            >
                                <OrgSpecialistCard/>
                            </Col>
                        ))
                    }
                </Row>
            </InfiniteScroll>
        </OfferGroupWrapper>
    )
}
