import React, {useCallback} from 'react'
import {RecordCardWrapper} from '../atoms'
import {Col, Row} from 'antd'
import {Title} from '../../../UIComponents/Typography/Title'
import {Text} from '../../../UIComponents/Typography/Text'
import {Avatar} from '../../../UIComponents/Avatar'
import {RecordsTimeSvg, TimerSvg} from '../../../Icons/Time'
import {EventSvg} from '../../../Icons/Event'
import {ShieldDoneSvg} from '../../../Icons/Shield'
import {StarSvg} from '../../../Icons/Start'
import {SchoolSvg} from '../../../Icons/School'
import {OfficialSvg} from '../../../Icons/Official'
import {MilitaryTechSvg} from '../../../Icons/MilitaryTech'

const cardIconArr = [
    {
        name: 'meet',
        icon: RecordsTimeSvg
    },
    {
        name: 'date',
        icon: EventSvg
    },
    {
        name: 'pending',
        icon: TimerSvg
    },
    {
        name: 'finished',
        icon: ShieldDoneSvg
    },
    {
        name: 'approved',
        icon: StarSvg
    },
    {
        name: 'awaiting',
        icon: SchoolSvg
    },
    {
        name: 'canceled',
        icon: OfficialSvg
    },
    {
        name: 'unregistered',
        icon: MilitaryTechSvg
    }
]


export const RecordCard = ({name, category, srs, status, meet, date}) => {

    const generateIcons = useCallback((item) => {
        switch (item) {
            case item.name === 'meet':
                return meet
            case item.name === 'date':
                return date

            default:
                return null
        }
    }, [])

    return (
        <RecordCardWrapper>
            <Row gutter={[0, 12]}>
                <Col span={24}>
                    <Row wrap={false} gutter={[4, 0]}>
                        <Col>
                            <Title>{name}</Title>
                            <Text>{category}</Text>
                        </Col>
                        <Col>
                            <Avatar size={36} imgUrl={srs}/>
                        </Col>
                    </Row>
                </Col>
                <Col span={24}>
                    <Row wrap={false}>
                        {
                            cardIconArr.map(item => {
                                const Icon = item.icon

                                return (
                                    <Col key={item.icon}>
                                        <Icon/>
                                        {generateIcons(item.name)}
                                    </Col>
                                )
                            })
                        }
                    </Row>
                </Col>
            </Row>
        </RecordCardWrapper>
    )
}
