import React, {useState} from 'react'
import {OrgSearchInputWrapper} from '../atoms'
import {Input} from '../../../UIComponents/Inputs'
import {allOrgMount} from '../../../Models/org-model'
import {AllScreenModal, IconBox} from '../../../UIComponents/GlobalStyles'
import {Col, Row} from 'antd'
import {ArrowLeftSvg} from '../../../Icons/Arrow'
import {CloseSvg} from '../../../Icons/Close'

export const OrgsSearchInput = ({onCancel, visible}) => {
    const [orgSearch, setOrgSearch] = useState('')
    const handleSubmit = (e) => {
        e.preventDefault()
        if (orgSearch.length > 0) {
            const data = {
                clear: true,
                params: {
                    search: orgSearch
                }
            }
            allOrgMount(data)
        }
    }

    return (
        <AllScreenModal
            width='100%'
            title={false}
            closable={true}
            onCancel={onCancel}
            visible={visible}
            destroyOnClose
        >
            <OrgSearchInputWrapper onSubmit={(e) => handleSubmit(e)}>
                <Row gutter={[12, 0]} justify='space-between' wrap={false} align='middle'>
                    <Col>
                        <IconBox>
                            left
                            <ArrowLeftSvg/>
                        </IconBox>
                    </Col>
                    <Col flex={1}>
                        <Input
                            onChange={(e) => setOrgSearch(e.target.value)}
                        />
                    </Col>
                    <Col>
                        <IconBox>
                            close
                            <CloseSvg/>
                        </IconBox>
                    </Col>
                </Row>
            </OrgSearchInputWrapper>
        </AllScreenModal>
    )
}
