import React, {useCallback} from 'react'
import {RecordCardWrapper} from '../atoms'
import {Col, Row} from 'antd'
import {Title} from '../../../UIComponents/Typography/Title'
import {Text} from '../../../UIComponents/Typography/Text'
import {Avatar} from '../../../UIComponents/Avatar'
import {RecordsTimeSvg, TimerSvg} from '../../../Icons/Time'
import {EventSvg} from '../../../Icons/Event'
import {ShieldDoneSvg} from '../../../Icons/Shield'
import {StarSvg} from '../../../Icons/Start'
import {SchoolSvg} from '../../../Icons/School'
import {OfficialSvg} from '../../../Icons/Official'
import {MilitaryTechSvg} from '../../../Icons/MilitaryTech'
import {useTranslation} from 'react-i18next'

const cardIconArr = [
    {
        name: 'pending',
        icon: TimerSvg
    },
    {
        name: 'finished',
        icon: ShieldDoneSvg
    },
    {
        name: 'approved',
        icon: StarSvg
    },
    {
        name: 'awaiting',
        icon: SchoolSvg
    },
    {
        name: 'canceled',
        icon: OfficialSvg
    },
    {
        name: 'unregistered',
        icon: MilitaryTechSvg
    }
]


export const RecordCard = ({name, category, srs, status, meet, date}) => {
    const {t} = useTranslation()

    const generateStatus = useCallback((statuses) => {
        switch (statuses) {
            case  1:
                return <div><StarSvg/>{t('approved')}</div>
            case 5:
                // return <div><ShieldDoneSvg/>{t('finished')}</div>
                return cardIconArr.find(item => item.name === 'finished' && item.name)

            default:
                return null
        }
    }, [t])


    return (
        <RecordCardWrapper>
            <Row gutter={[0, 12]}>
                <Col span={24}>
                    <Row wrap={false} gutter={[4, 0]}>
                        <Col>
                            <Title>{name}</Title>
                            <Text>{category}</Text>
                        </Col>
                        <Col>
                            <Avatar size={36} imgUrl={srs}/>
                        </Col>
                    </Row>
                </Col>
                <Col span={24}>
                    <Row wrap={false}>
                        <Col>
                            <RecordsTimeSvg/> {meet}
                        </Col>
                        <Col>
                            <EventSvg/> {date}
                        </Col>
                        <Col>
                            {generateStatus(status)}
                        </Col>
                    </Row>
                </Col>
            </Row>
        </RecordCardWrapper>
    )
}
