import React from 'react'
import {RootContent} from '../../../UIComponents/GlobalStyles'
import {FixedHeader} from '../../../Components/FixedHeader'
import {useGoBack} from '../../../Hooks/app'
import {useParams} from 'react-router-dom'
import {useTranslation} from 'react-i18next'
import {useStore} from 'effector-react'
import {$accountModel} from '../../../Models/account-model'
import {Col, Row} from 'antd'
import {Text} from '../../../UIComponents/Typography/Text'
import {AboutMeItemWrapper} from '../atoms'

export const AccountInformationPage = () => {
    const {t} = useTranslation()
    const {username} = useParams()
    const {$accountInfo: {data}} = useStore($accountModel)
    const {goBack} = useGoBack({pathname: `/@${username}`})
    const userInfo = data && data?.[username]

    return (
        <RootContent paddingTop={50}>
            <FixedHeader
                goBack={goBack}
                title={t('about_me')}
            />
            {
                userInfo && (
                    <Row gutter={[0, 12]}>
                        <Col span={24}>
                            <AboutMeItemWrapper>
                                <Text className='item-description'>{t('full_name')}</Text>
                                <Text level={5}>{`${userInfo.name} ${userInfo.lastname}`}</Text>
                            </AboutMeItemWrapper>
                        </Col>
                    </Row>
                )
            }
        </RootContent>
    )
}
