import React from 'react'
import {Col, Row} from 'antd'
import {SearchLinksItem} from '../../style'
import {useTranslation} from 'react-i18next'
import {searchByItems} from '../../../../data'
import {ArrowLeftSvg} from '../../../../Icons/Arrow'
import {CloseMiniSvg} from '../../../../Icons/Close'
import {allOrgMount} from '../../../../Models/org-model'
import {IconBox} from '../../../../UIComponents/GlobalStyles'
import {searchListMount} from '../../../../Models/search-model'
import {offeringsListMount} from '../../../../Models/offerings-model'

const initialParams = {
    limit: 18,
    offset: 0
}
export const SearchFixedHeader = ({setOpenSearch, active, setActive, setSearchText, searchText}) => {
    const {t} = useTranslation()

    const handleSubmit = (e) => {
        e.preventDefault()
        if (searchText.trim().length > 2) {
            const params = {
                params: {
                    ...initialParams,
                    search: searchText
                }
            }
            searchListMount(params)
        }
    }

    const handleItemsClick = (item) => {
        setActive(item.name)
        if (searchText.trim().length > 2) {
            const params = {
                clear: true,
                ...initialParams,
                search: searchText
            }
            if (item.name === 'organization') {
                allOrgMount({params})
            } else if (item.name === 'offerings') {
                offeringsListMount({params})
            }
        }
    }


    return (
        <form onSubmit={(e) => handleSubmit(e)}>
            <Row
                align='middle'
                gutter={[12, 0]}
                justify='space-between'
                className='fixed-header-search'
            >
                <Col onClick={() => setOpenSearch(false)}>
                    <IconBox>
                        <ArrowLeftSvg/>
                    </IconBox>
                </Col>
                <Col flex={1}>
                    <input
                        type='text'
                        value={searchText}
                        onChange={(e) => setSearchText(e.target.value)}
                    />
                </Col>
                <Col onClick={() => setSearchText('')}>
                    <IconBox>
                        <CloseMiniSvg/>
                    </IconBox>
                </Col>
            </Row>
            <Row>
                <Col span={24} className='search-items-block'>
                    <Row gutter={[0, 12]} className='search-items-wrapper'>
                        {
                            searchByItems.map((item, idx) => (
                                <Col key={`${idx + 1}`}>
                                    <SearchLinksItem
                                        color={active === item.name ? 'var(--primary-dwed)' : ''}
                                        onClick={() => handleItemsClick(item)}
                                    >
                                        {t(item.name)}
                                    </SearchLinksItem>
                                </Col>
                            ))
                        }
                    </Row>
                </Col>
            </Row>
        </form>
    )
}
