import {useEffect} from 'react'
import {URL_KEYS} from '../../Constants'
import {useOfferingDetail} from './use-offering-detail'
import {useHistory} from 'react-router-dom'

export function useOfferingSpecIdPush() {
    const {res} = useOfferingDetail()
    const {push} = useHistory()

    useEffect(() => {
        let timeout = null

        timeout = setTimeout(() => {
            if (!urlData[URL_KEYS.SPECIALIST_ID]) {
                if (res?.data?.responsible?.length > 0) {
                    push(`?${URL_KEYS.SPECIALIST_ID}=${res?.data?.responsible?.[0].id}`)
                }
            }
        }, 300)

        return () => {
            clearTimeout(timeout)
            timeout = null
        }
    }, [res, urlData, push])
}
