import React, {useCallback, useState} from 'react'
import {IconBox, RootContent} from '../../../UIComponents/GlobalStyles'
import {FixedHeader} from '../../../Components/FixedHeader'
import {useGoBack} from '../../../Hooks/app'
import {useParams} from 'react-router-dom'
import {useTranslation} from 'react-i18next'
import {useStore} from 'effector-react'
import {$accountModel} from '../../../Models/account-model'
import {Col, Row} from 'antd'
import {Text} from '../../../UIComponents/Typography/Text'
import {AboutAccountWrapper, AboutMeItemWrapper} from '../atoms'
import moment from 'moment'
import {ProfileSvg} from '../../../Icons/People'
import {InputUI} from '../../../UIComponents/mu-inputs'
import {useProfileInfo} from '../../../Hooks/account'
import {ArrowIosBottomSvg} from '../../../Icons/Arrow'
import {LISTS} from '../../../Constants'
import {Modal} from '../../../UIComponents/Modal'
import {SelectionList} from '../../../Components/SelectionList'

export const AccountInformationPage = () => {
    const {formik} = useProfileInfo()
    const {t} = useTranslation()
    const {username} = useParams()
    const [listType, setListType] = useState(LISTS.USER_CATEGORY)
    const {$accountInfo: {data}} = useStore($accountModel)
    const [modalIsOpen, setModalIsOpen] = useState(false)
    const {goBack} = useGoBack({pathname: `/@${username}`})

    const getTitle = useCallback(() => {
        let tmp = ''
        if (listType === LISTS.USER_CATEGORY) {
            tmp = 'category'
        }

        if (listType === LISTS.REGION) {
            tmp = 'region'
        }

        return tmp
    }, [listType])

    const handleClick = (listType) => {
        setListType(listType)
        setModalIsOpen(true)
    }

    return (
        <RootContent paddingTop={50}>
            <Modal
                centered
                modalIsOpen={modalIsOpen}
                title={t(`${getTitle()}`)}
                setModalIsOpen={setModalIsOpen}
                component={
                    <SelectionList
                        listType={listType}
                        onClose={() => setModalIsOpen(false)}
                        onChange={(value) => formik.setFieldValue(getTitle(), value)}
                    />
                }
            />
            <FixedHeader
                goBack={goBack}
                title={t('about_me')}
            />
            <AboutAccountWrapper>
                <Row gutter={[0, 12]}>
                    <Col span={24} className='change-photo'>
                        <IconBox color='var(--grey-dwed)'>
                            <ProfileSvg/>
                        </IconBox>
                        <Text color='var(--primary-dwed)'>{t('change_profile_photo')}</Text>
                    </Col>
                    <Col span={24}>
                        <InputUI
                            name='phone'
                            label={t('phone_number')}
                            value={formik?.values?.phone && `+${formik?.values?.phone}`}
                        />
                    </Col>
                    <Col span={24}>
                        <InputUI
                            inputType='text'
                            label={t('email')}
                            value={formik?.values?.email}
                            onChange={(e) => formik.setFieldValue('email', e.target.value)}
                        />
                    </Col>
                    <Col span={24}>
                        <InputUI
                            inputType='date'
                            value={formik?.values?.birthday}
                            label={t('birthday')}
                        />
                    </Col>
                    <Col span={24}>
                        <InputUI
                            inputType='select'
                            value={formik.values.user_lang}
                            label={t('language')}
                            options={langData}
                            onChange={(e) => formik.setFieldValue('user_lang', e.target.value)}
                        />
                    </Col>
                    <Col span={24}>
                        <InputUI
                            name='category'
                            label={t('category')}
                            icon={<ArrowIosBottomSvg/>}
                            value={formik?.values?.category?.name || ''}
                            onClick={() => handleClick(LISTS.USER_CATEGORY)}
                        />
                    </Col>
                    <Col span={24}>
                        <InputUI
                            name='region'
                            label={t('region')}
                            icon={<ArrowIosBottomSvg/>}
                            value={formik?.values?.region?.name || ''}
                            onClick={() => handleClick(LISTS.REGION)}
                        />
                    </Col>
                </Row>
                {
                    data && (
                        <Row gutter={[0, 12]}>
                            <Col span={24}>
                                <AboutMeItemWrapper>
                                    <Text className='item-name'>{t('full_name')}</Text>
                                    <Text level={5}>{`${data.name} ${data.lastname} ${data.surname}`}</Text>
                                </AboutMeItemWrapper>
                            </Col>
                            <Col span={24}>
                                <AboutMeItemWrapper>
                                    <Text className='item-name'>{t('specialist')}</Text>
                                    <Text level={5}>{data.main_cat.name}</Text>
                                </AboutMeItemWrapper>
                            </Col>
                            <Col span={24}>
                                <AboutMeItemWrapper>
                                    <Text className='item-name'>{t('birth_date')}</Text>
                                    <Text level={5}>{moment(data.birthday).format('DD.MM.YYYY')}</Text>
                                </AboutMeItemWrapper>
                            </Col>
                            <Col span={24}>
                                <AboutMeItemWrapper>
                                    <Text className='item-name'>{t('region')}</Text>
                                    <Text level={5}>{data.region.name}</Text>
                                </AboutMeItemWrapper>
                            </Col>
                        </Row>
                    )
                }
            </AboutAccountWrapper>
        </RootContent>
    )
}
